import React, { Component } from 'react';
import {Cotainer} from 'react-bootstrap';
import josh from '../josh.png'

const initData = {
    pre_heading: "NetStorm",
    heading: "Discover, collect, and sell extraordinary NFTs",
    content: "Explore on the world's best & largest NFT marketplace",
    btn_1: "Explore",
    btn_2: "Create"
}

class Hero extends Component {
    state = {
        data: {}
    }
    componentDidMount(){
        this.setState({
            data: initData
        })
    }
    render() {
        return (
            <section className="hero-section">
                  <div>
                    <div className="row">
                        <div >
                            
                            <h1 style={{fontSize:'127px',color:'#57048A'}}>DISCOVER, COLLECT AND SELL <br/>EXTRAORDINARY NFTs</h1>
                            <h3 style={{fontSize:'40px'}}>Like Never Before</h3>
                            {/* Buttons */}
                            <div className="button-group">
                                <a className="btn btn-bordered-white" href="/explore-1"><i className="icon-rocket mr-2" />{this.state.data.btn_1}</a>
                             </div>
                        </div>
                    </div>
                </div>
                {/* Shape */}
                <div className="shape">
                  
                </div>
            </section>
        );
    }
}

export default Hero;
