import React, { Component } from 'react';
import josh from '../josh.png'

const initData = {
    pre_heading: "NetStorm",
    heading: "Discover, collect, and sell extraordinary NFTs",
    content: "Explore on the world's best & largest NFT marketplace",
    btn_1: "Explore",
    btn_2: "Create"
}

class Hero extends Component {
    state = {
        data: {}
    }
    componentDidMount(){
        this.setState({
            data: initData
        })
    }
    render() {
        return (
            <section className="hero-section">
                  <div className="container">
                    <div className="row">
                        <div className="col-12 col-md-6 col-lg-7">
                            <span>{this.state.data.pre_heading}</span>
                            <h2 className="mt-4">Discover, collect, and sell extraordinary NFTs</h2>
                            <h5>{this.state.data.content}</h5>
                            {/* Buttons */}
                            <div className="button-group">
                                <a className="btn btn-bordered-white" href="/explore-1"><i className="icon-rocket mr-2" />{this.state.data.btn_1}</a>
                             </div>
                        </div>
                    </div>
                </div>
                {/* Shape */}
                <div className="shape">
                    
                </div>
            </section>
        );
    }
}

export default Hero;
